import { validateInfrastructureConfig } from '@motiadev/core';
import colors from 'colors';
import path from 'path';
export const infrastructureConfigsValidator = (builder) => {
    const errors = [];
    const eventSteps = Object.values(builder.stepsConfig).filter((step) => step.config.type === 'event' && step.config.infrastructure);
    for (const step of eventSteps) {
        const config = step.config;
        const relativePath = path.relative(builder.projectDir, step.filePath);
        const validationResult = validateInfrastructureConfig(config.infrastructure);
        if (!validationResult.success && validationResult.errors) {
            for (const error of validationResult.errors) {
                errors.push({
                    relativePath,
                    message: [
                        `Infrastructure configuration error in step ${colors.magenta(step.config.name)}:`,
                        `  ${colors.red('âžœ')} ${error.path}: ${colors.red(error.message)}`,
                    ].join('\n'),
                    step,
                });
            }
        }
    }
    return { errors, warnings: [] };
};
