import { isApiStep, isCronStep, isEventStep, trackEvent, } from '@motiadev/core';
import { existsSync } from 'fs';
import path from 'path';
import { Watcher } from './watcher';
const setupWatcherHandlers = (watcher, lockedData, server, eventHandler, cronManager) => {
    watcher.onStreamChange((oldStream, stream) => {
        trackEvent('stream_updated', {
            streamName: stream.config.name,
            type: stream.config.baseConfig.storageType,
        });
        return lockedData.updateStream(oldStream, stream);
    });
    watcher.onStreamCreate((stream) => {
        trackEvent('stream_created', {
            streamName: stream.config.name,
            type: stream.config.baseConfig.storageType,
        });
        return lockedData.createStream(stream);
    });
    watcher.onStreamDelete((stream) => {
        trackEvent('stream_deleted', {
            streamName: stream.config.name,
            type: stream.config.baseConfig.storageType,
        });
        return lockedData.deleteStream(stream);
    });
    watcher.onStepChange((oldStep, newStep) => {
        if (isApiStep(oldStep))
            server.removeRoute(oldStep);
        if (isCronStep(oldStep))
            cronManager.removeCronJob(oldStep);
        if (isEventStep(oldStep))
            eventHandler.removeHandler(oldStep);
        const isUpdated = lockedData.updateStep(oldStep, newStep);
        if (isUpdated) {
            trackEvent('step_updated', {
                stepName: newStep.config.name,
                type: newStep.config.type,
            });
            if (isCronStep(newStep))
                cronManager.createCronJob(newStep);
            if (isEventStep(newStep))
                eventHandler.createHandler(newStep);
            if (isApiStep(newStep))
                server.addRoute(newStep);
        }
    });
    watcher.onStepCreate((step) => {
        const isCreated = lockedData.createStep(step);
        if (isCreated) {
            trackEvent('step_created', {
                stepName: step.config.name,
                type: step.config.type,
            });
            if (isApiStep(step))
                server.addRoute(step);
            if (isEventStep(step))
                eventHandler.createHandler(step);
            if (isCronStep(step))
                cronManager.createCronJob(step);
        }
    });
    watcher.onStepDelete((step) => {
        trackEvent('step_deleted', {
            stepName: step.config.name,
            type: step.config.type,
        });
        if (isApiStep(step))
            server.removeRoute(step);
        if (isEventStep(step))
            eventHandler.removeHandler(step);
        if (isCronStep(step))
            cronManager.removeCronJob(step);
        lockedData.deleteStep(step);
    });
};
export const createDevWatchers = (lockedData, server, eventHandler, cronManager) => {
    const baseDir = process.cwd();
    const dirs = [path.join(baseDir, 'steps'), path.join(baseDir, 'src')];
    const watchers = [];
    dirs.forEach((dir) => {
        if (existsSync(dir)) {
            const watcher = new Watcher(dir, lockedData);
            setupWatcherHandlers(watcher, lockedData, server, eventHandler, cronManager);
            watcher.init();
            watchers.push(watcher);
        }
    });
    return {
        stop: async () => {
            await Promise.all(watchers.map((watcher) => watcher.stop()));
        },
    };
};
